import React from "react";
import { useSelector } from "react-redux";

const ProductComponent = () => {
  const products = useSelector((state) => state.allProducts.products);
  const renderList = products.map((Product)=>{
      const {id , title ,image , price ,category} = Product;
      return(

    <ul className="row" style={{display:"flex",flexWrap:"wrap"}} key={id}>
    <ul class="row row-cols-1 row-cols-md-1 g-4">
    <ul className="card" style={{width:"220px"}}>
    <img src={image} className="card-img-top" alt={title} style={{width:"200px",height:"250px"}}/>
      <ul className="card-body">
        <h5 className="card-title">{title}</h5>
        <p className="card-text">${price}</p>
        <p className="card-text">{category}</p>
        {/* <a href="#" class="btn btn-primary">Submit</a> */}
      </ul>
    </ul>
  </ul>
</ul>




      )
  })
  return (
    <>
{renderList}
  </>

  )
};

export default ProductComponent;
